mapper.select:
SELECT r.RES_NO AS resNo, u.USER_ID AS userId, s.STORE_NAME AS storeName, r.RES_DATE AS date, r.RES_TIME AS time, r.QR, rev.REVIEW_NO AS replyNo, 0 AS reservation_type FROM TBL_RESERVATION r LEFT JOIN TBL_USER u ON r.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON r.STORE_NO = s.STORE_NO LEFT JOIN TBL_REVIEW rev ON r.RES_NO = rev.RES_NO WHERE u.USER_ID = #{userId}
UNION ALL
SELECT er.RES_NO AS resNo, u.USER_ID AS userId, s.STORE_NAME AS storeName, er.RES_DATE AS date, er.RES_TIME AS time, er.QR, rev.REVIEW_NO AS replyNo, 1 AS reservation_type FROM TBL_END_RES er LEFT JOIN TBL_USER u ON er.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON er.STORE_NO = s.STORE_NO LEFT JOIN TBL_REVIEW rev ON er.RES_NO = rev.RES_NO WHERE u.USER_ID = #{userId}
ORDER BY reservation_type ASC, date ASC, time ASC;

SELECT r.RES_NO AS resNo, u.USER_ID AS userId, s.STORE_NAME AS storeName, r.RES_DATE AS date, r.RES_TIME AS time, r.QR, 0 AS reservation_type FROM TBL_RESERVATION r LEFT JOIN TBL_USER u ON r.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON r.STORE_NO = s.STORE_NO WHERE u.USER_ID = #{userId}
AND s.STORE_NAME LIKE CONCAT('%', #{search}, '%')
UNION
SELECT er.RES_NO AS resNo, u.USER_ID AS userId, s.STORE_NAME AS storeName, er.RES_DATE AS date, er.RES_TIME AS time, er.QR, 1 AS reservation_type FROM TBL_END_RES er LEFT JOIN TBL_USER u ON er.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON er.STORE_NO = s.STORE_NO WHERE u.USER_ID = #{userId}
AND s.STORE_NAME LIKE CONCAT('%', #{search}, '%')
ORDER BY reservation_type ASC, date ASC, time ASC;

SELECT u.USER_NO AS userNo, u.USER_ID AS userId, s.STORE_NAME AS storeName, s.STORE_NO AS storeNo, r.REVIEW_NO AS reviewNo, r.REVIEW_DATE AS reviewDate, r.REVIEW_CONTENT AS reviewText, r.REVIEW_SCOPE AS star FROM TBL_REVIEW r LEFT JOIN TBL_USER u ON u.USER_NO = r.USER_NO LEFT JOIN TBL_STORE s ON s.STORE_NO = r.STORE_NO WHERE u.USER_ID = #{userId}
AND s.STORE_NAME LIKE CONCAT('%', #{search}, '%')
ORDER BY reviewNo DESC;

SELECT u.USER_ID AS userId, u.USER_NO AS userNo, i.INQ_NO AS inquiryNo, i.INQ_TITLE AS title, i.INQ_CONTENT AS text, i.INQ_DATE AS inquiryDate, i.ANS_TITLE AS answerTitle, i.ANS_CONTENT AS answerContent, i.ANS_DATE AS answerDate, i.FILE AS file, i.STATE AS inquiryState, c.CATEGORY_NO AS categoryNo, c.CATEGORY_NAME AS category FROM TBL_INQUIRY i LEFT JOIN TBL_USER u ON u.USER_NO = i.USER_NO LEFT JOIN TBL_INQUIRY_CATEGORY c ON c.CATEGORY_NO = i.CATEGORY_NO WHERE u.USER_ID = #{userId}
AND i.INQ_TITLE LIKE CONCAT('%', #{search}, '%')
ORDER BY i.INQ_NO DESC;

mapper.update:
UPDATE TBL_INQUIRY
SET STATE = #{inquiryState}
WHERE INQ_NO = #{inquiryNo}

UPDATE TBL_INQUIRY
SET INQ_TITLE = #{title},
INQ_CONTENT = #{text},
FILE = #{file}
WHERE INQ_NO = #{inquiryNo}

mapper.select:
SELECT USER_PWD
FROM TBL_USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE TBL_USER


        USER_NAME = #{userName},


        USER_PWD = #{userPass},


        USER_PROFILE = #{file}


WHERE USER_ID = #{userId}

UPDATE TBL_USER
SET USER_PROFILE = #{file}
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_PROFILE
FROM TBL_USER
WHERE USER_ID = #{userId}

SELECT r.RES_NO AS resNo, u.USER_ID AS userId, u.USER_NAME AS userName, s.STORE_NO AS storeNo, s.STORE_NAME AS storeName, r.RES_DATE AS date, r.RES_TIME AS time, r.QR FROM TBL_RESERVATION r LEFT JOIN TBL_USER u ON r.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON r.STORE_NO = s.STORE_NO WHERE r.RES_NO = #{resNo}
UNION
SELECT er.RES_NO AS resNo, u.USER_ID AS userId, u.USER_NAME AS userName, s.STORE_NO AS storeNo, s.STORE_NAME AS storeName, er.RES_DATE AS date, er.RES_TIME AS time, er.QR FROM TBL_END_RES er LEFT JOIN TBL_USER u ON er.USER_NO = u.USER_NO LEFT JOIN TBL_STORE s ON er.STORE_NO = s.STORE_NO WHERE er.RES_NO = #{resNo}

SELECT USER_NO
FROM TBL_USER
WHERE USER_ID = #{userId, jdbcType=VARCHAR}

SELECT NOSHOW
FROM TBL_USER
WHERE USER_NO = #{userNo, jdbcType=BIGINT}

SELECT EMAIL
FROM TBL_USER
WHERE USER_NO = #{userNo, jdbcType=BIGINT}

SELECT USER_NO, EMAIL, NOSHOW
FROM TBL_USER
WHERE USER_ID = #{userId}
